import base64

def save_qr_image(base64_data, output_path="decoded_qr_image.jpeg"):
    """base64 인코딩된 QR 코드를 이미지 파일로 디코딩하여 저장합니다."""

    # "data:image/jpeg;base64," 부분 제거
    base64_image_str = base64_data.split(",")[1]  # 첫 번째 부분은 제거된 헤더

    # base64 문자열 디코딩
    image_data = base64.b64decode(base64_image_str)

    # 디코딩된 데이터를 이미지 파일로 저장
    with open(output_path, "wb") as image_file:
        image_file.write(image_data)

    print(f"QR 코드 이미지가 {output_path}로 저장되었습니다.")

# 사용 예시
base64_qr_code = "data:image/jpeg;base64,"  # 예시 QR 코드 (앞부분만 표시)
save_qr_image(base64_qr_code)



if __name__ == '__main__':
    save_qr_image()